from collections import defaultdict
import pandas as pd
import numpy as np
import json

# TODO: Learn about the origin of this data as for the bats, improve the structure of all files in this directory and rewrite such that it does not rely on the 21J reference sequence to decide whether a site is synonymous

# Open nextclade file with deer sequences aligned to reference SARS-CoV-2 tree
with open('data/nextclade.auspice.json', 'r') as f:
    data = json.load(f)
    ref_seq = data['root_sequence']['nuc']  # 29'903 nucleotides (Where does this come from?)
    # nextclade.auspice.json is a is a dictionary with keys 'version' ({str} v2), 'meta' ({dict: 11} extensions,
    # ...), 'tree' ({dict: 4} 'name', 'branch_attrs', 'node_attrs', 'children'), 'root_sequence' ({dict: 13}).
    deer_tree = data['tree']
    # deer_tree is a dict with keys 'name' ({str}), 'branch_attrs' ({dict: 2} 'mutations', 'labels'), 'node_attrs' (
    # 'clade_membership', ...), 'children' ({list: n_children})

# Clade founder sequence of 21J obtained from human_data/counts.csv (29'903 nts)
clade_founder_21J = ''
diff_count = np.count_nonzero(np.array(list(ref_seq)) != np.array(list(clade_founder_21J)))
print(f"- no. of positions differing between deer reference and 21J clade founder: {diff_count}/{len(ref_seq)}")

# Prepare container to store deer mutation counts
deer_count = defaultdict(int)

# Keep track of excluded mutations
incl_sites = defaultdict(int)
incl_nodes = defaultdict(int)
nt_errors = defaultdict(int)
site_errors = defaultdict(int)
attrs_not_av = defaultdict(int)
not_new = defaultdict(int)
zero_muts = defaultdict(int)

# Load data for synonymous, non-excluded mutations in a human clade (including those with zero actual counts!)
clade = '21J'
df_help = pd.read_csv('/Users/georgangehrn/Desktop/SARS2-mut-fitness-corr/human_data/counts/counts_all_'
                      + clade + '_nonex_syn.csv')


def collect_mutations(node):
    # Go recursively through deer tree
    if 'children' in node:
        for c in node['children']:
            collect_mutations(c)

    # Check that the node contains the fields 'node_attrs' and 'Node type'
    if 'node_attrs' in node and 'Node type' in node['node_attrs']:
        # Check that the node is new, i.e., that it is a deer and not a reference node
        if node['node_attrs']['Node type']['value'] == 'New':
            # Loop through all nucleotide (nuc) mutations on the branch leading to that node
            for mut in node['branch_attrs']['mutations']['nuc']:
                # Check that the mutation is synonymous and not excluded in 21J
                if mut in df_help['nt_mutation'].values:
                    deer_count[mut] += 1
                    incl_sites[mut[1:-1]] += 1
                    incl_nodes[node['name']] += 1
                # else:
                #     if mut[1:-1] in df_help['nt_mutation'].str[1:-1]:
                #         nt_errors[mut[1:-1]] += 1
                #     else:
                #         site_errors[mut[1:-1]] += 1
        else:
            not_new[node['name']] += 1
    else:
        attrs_not_av[node['name']] += 1


if __name__ == '__main__':

    '''
        This script extracts the mutation counts from the deer sequences and stores them in 'counts_deer.csv'.
    '''

    # Create dict with number of mutation counts (>0) for mutations that are synonymous and not excluded in 21J
    collect_mutations(deer_tree)

    # Print information
    print(f'- attribute "Node type" not available for {len(attrs_not_av)} nodes')
    print(f'- {len(not_new)} nodes not labeled as "New"')
    print(f'- {len(incl_nodes)} nodes included')
    print(f'- {len(incl_sites)} sites included')
    print(f'- {len(nt_errors)} nucleotide errors (= site available in human clade, but wrong ancestral nucleotide)')
    print(f'- {len(site_errors)} sites not in human clade data')

    # Add a zero count for all non-excluded, synonymous mutations in 21J data that agree in their ancestral nt
    for mut in df_help['nt_mutation']:
        if ref_seq[int(mut[1:-1]) - 1] == mut[0]:
            deer_count[mut] += 0
            zero_muts[mut] += 1

    print('- no. of added zero count mutations: ' + str(len(zero_muts)))

    # Create pandas dataframe
    d = {'nt_mutation': deer_count.keys(), 'actual_count': deer_count.values()}
    df = pd.DataFrame(data=d)
    df['nt_site'] = df['nt_mutation'].apply(lambda x: int(x[1:-1]))
    df['clade_founder_nt'] = df['nt_mutation'].apply(lambda x: x[0])
    df = df.sort_values('nt_site')

    # Get context
    nts = np.array(list(ref_seq))
    context = ['XXXXX'] * len(nts)
    for i in range(2, len(nts) - 2):
        context[i] = nts[i - 2] + nts[i - 1] + nts[i] + nts[i + 1] + nts[i + 2]

    # Convert back to a numpy array if needed
    context = np.array(context)
    df['context'] = np.array([context[df['nt_site'].values[i]-1] for i in range(len(df))])
    # Add secondary structure information (no exclusion of sites based on nt differences)
    df = pd.merge(df, df_help[['nt_mutation', 'unpaired']], on='nt_mutation', how='left')

    # Save as new csv file
    df.to_csv('counts_deer.csv', index=False, header=True)